const puppeteer = require('puppeteer');

(async () => {
  const browser = await puppeteer.launch({ headless: true });
  const page = await browser.newPage();
  await page.goto('https://www.honolulu.gov/clerk/clk-council-calendar/', { waitUntil: 'networkidle2' });

  // Find all event items
  const items = await page.$$('div.em-cal-event > div');

  for (const item of items) {
    // Click to open modal/popup
    await item.click();
    await page.waitForSelector('.em-modal-content', { timeout: 5000 });

    // Extract from modal
    const data = await page.evaluate(() => ({
      time: document.querySelector('div.em-item-meta-line.em-event-time')?.textContent,
      date: document.querySelector('div.em-item-meta-line.em-event-date')?.textContent,
      name: document.querySelector('div.em-modal-title > a')?.textContent,
      agenda_url: document.querySelector('a > span > strong').href,
      docket_url: document.querySelector('a > span > strong').href
    }));

    console.log(data);

    // Close modal
    await page.click('.close-button');
    await page.waitForTimeout(500);
  }

  await browser.close();
})();
