const puppeteer = require('puppeteer');

(async () => {
  const browser = await puppeteer.launch();
  const page = await browser.newPage();

  await page.goto('https://www.honolulu.gov/clerk/clk-council-calendar/');

  // Click on each event to open the modal
  const events = await page.$$('.em-cal-event > div');
  for (const event of events) {
    await event.click();
    await page.waitForSelector('div.em-modal-title'); // Wait for the modal to appear

    // Extract data from the modal and the event itself
    const time = await page.evaluate(el => el.textContent, await event.$('.em-item-meta-line.em-event-time'));
    const date = await page.evaluate(el => el.textContent, await event.$('.em-item-meta-line.em-event-date'));
    const name = await page.evaluate(el => el.getAttribute('href'), await page.$('div.em-modal-title > a'));

    // Add more fields here following the same pattern

    console.log({ time, date, name });
  }

  await browser.close();
})();
